fn main() {
    println!("Advent of code 2017.");
    println!("--------------------------------------------------------------------------------");

    do_inverse_captcha();
}

fn do_inverse_captcha() {
    println!("Day 1-1\n");

    let real_input = "";

    let result = inverse_captcha(&String::from("1122"), 1);
    println!("3={0}, {1}", result, result==3);
    let result = inverse_captcha(&String::from("1111"), 1);
    println!("4={0}, {1}", result, result==4);
    let result = inverse_captcha(&String::from("1234"), 1);
    println!("0={0}, {1}", result, result==0);
    let result = inverse_captcha(&String::from("91212129"), 1);
    println!("9={0}, {1}", result, result==9);
    let result = inverse_captcha(&String::from(real_input), 1);
    println!("Real: {0}", result);
    println!("--------------------------------------------------------------------------------");

    println!("Day 1-2\n");

    let input = String::from("1212");
    let stepsize = input.chars().count()/2;
    let result = inverse_captcha(&input, stepsize);
    println!("6={0}, {1}", result, result==6);

    let input = "1221";
    let stepsize = input.chars().count()/2;
    let result = inverse_captcha(&String::from(input), stepsize);
    println!("0={0}, {1}", result, result==0);

    let input = "123425";
    let stepsize = input.chars().count()/2;
    let result = inverse_captcha(&String::from(input), stepsize);
    println!("4={0}, {1}", result, result==4);

    let input = "123123";
    let stepsize = input.chars().count()/2;
    let result = inverse_captcha(&String::from(input), stepsize);
    println!("12={0}, {1}", result, result==12);

    let input = "12131415";
    let stepsize = input.chars().count()/2;
    let result = inverse_captcha(&String::from(input), stepsize);
    println!("4={0}, {1}", result, result==4);

    let stepsize = real_input.chars().count()/2;
    let result = inverse_captcha(&String::from(real_input), stepsize);
    println!("Real: {0}", result);
}

fn inverse_captcha(input: &String, step: usize) -> u32 {
    let step = step as usize;
    let input_converted: Vec<u32> = input.chars().map(|c| c.to_digit(10).unwrap()).collect();

    let mut sum = 0;
    for i in 0..input_converted.len() {
        let current = input_converted[i];
        let i_next = (i+step) % input_converted.len();
        let next = input_converted[i_next];
        if current == next {
            sum += current;
        }
    }
    sum
}
